import React, { useState, useEffect } from 'react';
import axios from 'axios';

function QuizListTable() {
    const [quizzes, setQuizzes] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:8000/api/quizzes/');
                setQuizzes(Array.isArray(response.data) ? response.data : response.data.results || []);
            } catch (error) {
                setError(error);
            } finally {
                setLoading(false);
            }
        };
        fetchData();
    }, []);

    const tableStyle = {
        width: '100%',
        borderCollapse: 'collapse',
        marginTop: '20px'
    };

    const cellStyle = {
        border: '1px solid #ddd',
        padding: '8px',
        textAlign: 'left'
    };

    const headerStyle = {
        backgroundColor: '#f4f4f4',
        fontWeight: 'bold',
        ...cellStyle
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error loading quizzes</div>;

    return (
        <div style={{ padding: '20px' }}>
            <h2 style={{ marginBottom: '20px' }}>Quiz List</h2>
            <table style={tableStyle}>
                <thead>
                    <tr>
                        <th style={headerStyle}>ID</th>
                        <th style={headerStyle}>Title</th>
                        <th style={headerStyle}>Description</th>
                        <th style={headerStyle}>Created At</th>
                    </tr>
                </thead>
                <tbody>
                    {quizzes.map(quiz => (
                        <tr key={quiz.id}>
                            <td style={cellStyle}>{quiz.id}</td>
                            <td style={cellStyle}>{quiz.title}</td>
                            <td style={cellStyle}>{quiz.description}</td>
                            <td style={cellStyle}>
                                {new Date(quiz.created_At).toLocaleDateString()}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default QuizListTable;
