import React, { useState, useEffect } from 'react';
import axios from 'axios';


function QuizListTable() {
    const [quizzes, setQuizzes] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:8000/api/quizzes/');
                console.log("Fetched quizzes data:", response.data);
                // Handle both array and paginated object response
                if (Array.isArray(response.data)) {
                    setQuizzes(response.data);
                } else if (response.data.results) {
                    setQuizzes(response.data.results);
                } else {
                    setQuizzes([]);
                }
            } catch (error) {
                console.error("Fetch error:", error);
                setError(error);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    if (loading) {
        return <div>Loading Quizzes...</div>;
    }

    if (error) {
        return <div>Error: {error.message}</div>;
    }

    // Debug: Show quizzes length and first quiz object
    console.log("Rendering table, quizzes length:", quizzes.length, quizzes[0]);

    return (
        <div>
            <h2>Quiz List</h2>
            <div>
                {/* Debug output */}
                <pre style={{background: "#eee", padding: "8px", fontSize: "12px"}}>
                    {JSON.stringify(quizzes, null, 2)}
                </pre>
            </div>
            <table border="1">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Title</th>
                        <th>Description</th>
                        <th>Created At</th>
                        <th>Questions Count</th>
                    </tr>
                </thead>
                <tbody>
                    {quizzes.map((quiz) => (
                        <tr key={quiz.id}>
                            <td>{quiz.id}</td>
                            <td>{quiz.title}</td>
                            <td>{quiz.description}</td>
                            <td>{quiz.created_At}</td>
                            <td>{quiz.questions ? quiz.questions.length : 0}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default QuizListTable;
