import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './leaderboard.css';

const Leaderboard = ({ quizName }) => {
  const [scores, setScores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const url = quizName 
          ? `http://127.0.0.1:8000/api/leaderboard/${encodeURIComponent(quizName)}/`
          : 'http://127.0.0.1:8000/api/leaderboard/';
        
        const response = await axios.get(url);
        console.log('Leaderboard response:', response.data);

        if (quizName && response.data.scores) {
          setScores(response.data.scores);
        } else {
          // Handle the case when we get all quizzes
          const allScores = [];
          Object.entries(response.data).forEach(([quizName, quizScores]) => {
            allScores.push(...quizScores);
          });
          setScores(allScores);
        }
      } catch (err) {
        console.error('Leaderboard error:', err);
        setError('Unable to load leaderboard');
      } finally {
        setLoading(false);
      }
    };

    fetchLeaderboard();
  }, [quizName]);

  if (loading) {
    return (
      <div className="d-flex justify-content-center p-4">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger m-3" role="alert">
        Error loading leaderboard: {error}
      </div>
    );
  }

  return (
    <div className="card">
      <div className="card-header bg-primary text-white">
        <h5 className="mb-0">Leaderboard</h5>
      </div>
      <div className="card-body p-0">
        <table className="table table-hover mb-0">
          <thead>
            <tr>
              <th>#</th>
              <th>Player</th>
              <th>Score</th>
            </tr>
          </thead>
          <tbody>
            {scores.map((score, index) => (
              <tr key={score.id || index}>
                <td>{index + 1}</td>
                <td>{score.username}</td>
                <td>{score.score}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Leaderboard;
