import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './leaderboard.css';

const Leaderboard = () => {
  const [allScores, setAllScores] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/leaderboard/');
        setAllScores(response.data);
      } catch (err) {
        console.error('Error:', err);
      }
      setLoading(false);
    };

    fetchLeaderboard();
  }, []);

  if (loading) return <div>Loading...</div>;

  return (
    <div className="leaderboards-container">
      {Object.entries(allScores).map(([quizName, scores]) => (
        <div key={quizName} className="leaderboard-section">
          <div className="leaderboard-header">
            <div className="header-title">{quizName} - Top users</div>
            <div className="user-count">{scores.length} users</div>
          </div>
          
          <div className="leaderboard-body">
            {scores.length > 0 ? (
              <>
                <div className="table-header">
                  <div className="rank-col">Rank</div>
                  <div className="player-col">Player</div>
                  <div className="score-col">Score</div>
                </div>
                <div className="table-body">
                  {scores.map((score, index) => (
                    <div key={index} className="table-row">
                      <div className="rank-col">{index + 1}</div>
                      <div className="player-col">{score.username}</div>
                      <div className="score-col">
                        <div className="score-badge">{score.score}</div>
                      </div>
                    </div>
                  ))}
                </div>
              </>
            ) : (
              <div className="no-scores">No rankings available</div>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Leaderboard;
