import React, { useState, useEffect } from 'react';

function QuizListTable() {
    const [quizzes, setQuizzes] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('http://localhost:8000/api/quizzes/');
                console.log("Fetch response:", response);
                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }
                const data = await response.json();
                console.log("Fetched quizzes data:", data);
                // Handle both array and paginated object response
                if (Array.isArray(data)) {
                    setQuizzes(data);
                } else if (data.results) {
                    setQuizzes(data.results);
                } else {
                    setQuizzes([]);
                }
            } catch (error) {
                console.error("Fetch error:", error);
                setError(error);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    if (loading) {
        return <div>Loading Quizzes...</div>;
    }

    if (error) {
        return <div>Error: {error.message}</div>;
    }

    // Debug: Show quizzes length and first quiz object
    console.log("Rendering table, quizzes length:", quizzes.length, quizzes[0]);

    return (
        <div>
            <h2>Quiz List</h2>
            <table border="1">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Title</th>
                        <th>Description</th>
                        <th>Created At</th>
                        <th>Questions Count</th>
                    </tr>
                </thead>
                <tbody>
                    {quizzes.map((quiz) => (
                        <tr key={quiz.id}>
                            <td>{quiz.id}</td>
                            <td>{quiz.title}</td>
                            <td>{quiz.description}</td>
                            <td>{quiz.created_At}</td>
                            <td>{quiz.questions ? quiz.questions.length : 0}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default QuizListTable;
