import React, { useEffect, useState } from 'react';// import React from 'react';
import axios from 'axios';


const Leaderboard = ({ quizName }) => {
  const [scores, setScores] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const url = quizName 
          ? `http://127.0.0.1:8000/api/leaderboard/${encodeURIComponent(quizName)}/`
          : 'http://127.0.0.1:8000/api/leaderboard/';
        
        const response = await axios.get(url);
        console.log('Leaderboard response:', response.data); // Debug log

        if (quizName && response.data.scores) {
          setScores({ [quizName]: response.data.scores });
        } else if (!quizName && typeof response.data === 'object') {
          setScores(response.data);
        } else {
          setScores({});
        }
      } catch (err) {
        console.error('Leaderboard error:', err);
        setError('Unable to load leaderboard');
        setScores({});
      } finally {
        setLoading(false);
      }
    };

    fetchLeaderboard();
  }, [quizName]);

  if (loading) {
    return (
      <div className="d-flex justify-content-center p-4">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger m-3" role="alert">
        Error loading leaderboard: {error}
      </div>
    );
  }

  return (
    <div /* className="container-fluid" */>
      {Object.entries(scores).map(([quizTitle, quizScores]) => (
        <div key={quizTitle} /* className="card shadow-sm mb-4" */ style={{border: '1px solid #ccc', marginBottom: '1rem', borderRadius: '8px'}}>
          <div /* className="card-header bg-primary text-white d-flex justify-content-between align-items-center" */ style={{background: '#007bff', color: '#fff', padding: '1rem', display: 'flex', justifyContent: 'space-between', alignItems: 'center'}}>
            <h5 /* className="card-title mb-0" */ style={{margin: 0}}>{quizTitle} - Top users</h5>
            <span /* className="badge bg-light text-primary" */ style={{background: '#f8f9fa', color: '#007bff', padding: '0.5em 1em', borderRadius: '1em'}}>
              {quizScores.length} user
            </span>
          </div>
          <div /* className="card-body p-0" */ style={{padding: 0}}>
            {quizScores.length > 0 ? (
              <div /* className="table-responsive" */ style={{overflowX: 'auto'}}>
                <table /* className="table table-hover mb-0" */ style={{width: '100%', marginBottom: 0}}>
                  <thead /* className="table-light" */ style={{background: '#f8f9fa'}}>
                    <tr>
                      <th /* className="text-center" */ style={{width: "10%", textAlign: 'center'}}>Rank</th>
                      <th style={{width: "50%"}}>Player</th>
                      <th /* className="text-end" */ style={{width: "40%", textAlign: 'right'}}>Score</th>
                    </tr>
                  </thead>
                  <tbody>
                    {quizScores.map((score, index) => (
                      <tr key={index} /* className={index < 3 ? 'table-warning' : ''} */ style={index < 3 ? {background: '#fff3cd'} : {}}>
                        <td
                          {...(index >= 3 ? { style: { paddingRight: '33px', textAlign: 'center' } } : { style: { textAlign: 'center' } })}
                        >
                          {index + 1}
                          {index < 3 && <span style={{marginLeft: '0.5em'}}>üèÜ</span>}
                        </td>
                        <td>
                          <span /* className="fw-semibold" */ style={{fontWeight: 600}}>{score.username}</span>
                        </td>
                        <td /* className="text-center" */ style={{textAlign: 'center'}}>
                          <span
                            /* className={`badge ${index < 3 ? 'bg-warning text-dark' : 'bg-success'}`} */
                            style={{
                              background: index < 3 ? '#ffc107' : '#198754',
                              color: index < 3 ? '#212529' : '#fff',
                              padding: '0.5em 1em',
                              borderRadius: '1em'
                            }}
                          >
                            {score.score}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              <div /* className="p-4 text-center text-muted" */ style={{padding: '2rem', textAlign: 'center', color: '#6c757d'}}>
                No scores available for {quizTitle}
              </div>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Leaderboard;
