import React, { useEffect, useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const Leaderboard = ({ quizId = 1, quizName = "General questions" }) => {
  const [scores, setScores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const url = `http://127.0.0.1:8000/api/leaderboard/${encodeURIComponent(quizName)}/`;
        const response = await axios.get(url);
        
        if (response.data && response.data.scores) {
          setScores(response.data.scores);
        } else {
          setScores([]);
        }
      } catch (err) {
        console.error('Leaderboard error:', err);
        setError('Unable to load leaderboard');
        setScores([]);
      } finally {
        setLoading(false);
      }
    };

    if (quizName) {
      fetchLeaderboard();
    }
  }, [quizName]);

  if (loading) {
    return (
      <div className="d-flex justify-content-center p-4">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger m-3" role="alert">
        Error loading leaderboard: {error}
      </div>
    );
  }

  return (
    <div className="card shadow-sm">
      <div className="card-header bg-primary text-white d-flex justify-content-between align-items-center">
        <h5 className="card-title mb-0">Top Players - {quizName}</h5>
        <span className="badge bg-light text-primary">{scores.length} Players</span>
      </div>
      <div className="card-body p-0">
        {scores.length > 0 ? (
          <div className="table-responsive">
            <table className="table table-hover mb-0">
              <thead className="table-light">
                <tr>
                  <th className="text-center" style={{width: "10%"}}>Rank</th>
                  <th style={{width: "50%"}}>Player</th>
                  <th className="text-end" style={{width: "40%"}}>Score</th>
                </tr>
              </thead>
              <tbody>
                {scores.map((score, index) => (
                  <tr key={index} className={index < 3 ? 'table-warning' : ''}>
                    <td className="text-center">
                      {index + 1}
                      {index < 3 && <span className="ms-1">🏆</span>}
                    </td>
                    <td>
                      <span className="fw-semibold">{score.username}</span>
                    </td>
                    <td className="text-end">
                      <span className={`badge ${index < 3 ? 'bg-warning text-dark' : 'bg-success'}`}>
                        {score.score}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <div className="p-4 text-center text-muted">
            No scores available yet for {quizName}
          </div>
        )}
      </div>
    </div>
  );
};

export default Leaderboard;
