import React, { useEffect, useState } from 'react';
import axios from 'axios';


const Leaderboard = ({ quizName }) => {
  const [scores, setScores] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const url = quizName 
          ? `http://127.0.0.1:8000/api/leaderboard/${encodeURIComponent(quizName)}/`
          : 'http://127.0.0.1:8000/api/leaderboard/';
        
        const response = await axios.get(url);
        console.log('Leaderboard response:', response.data); // Debug log

        if (quizName && response.data.scores) {
          setScores({ [quizName]: response.data.scores });
        } else if (!quizName && typeof response.data === 'object') {
          setScores(response.data);
        } else {
          setScores({});
        }
      } catch (err) {
        console.error('Leaderboard error:', err);
        setError('Unable to load leaderboard');
        setScores({});
      } finally {
        setLoading(false);
      }
    };

    fetchLeaderboard();
  }, [quizName]);

  if (loading) {
    return (
      <div className="d-flex justify-content-center p-4">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger m-3" role="alert">
        Error loading leaderboard: {error}
      </div>
    );
  }

  return (
    <div>
      {Object.entries(scores).map(([quizTitle, quizScores]) => (
        <div key={quizTitle} className="leaderboard-section">
          <div className="leaderboard-header">
            <span>{quizTitle} - Top users</span>
            <span className="user-count">{quizScores.length} users</span>
          </div>
          {quizScores.length > 0 ? (
            <div className="score-table">
              <div className="table-header">
                <div>Rank</div>
                <div>Player</div>
                <div>Score</div>
              </div>
              <div className="table-body">
                {quizScores.map((score, index) => (
                  <div key={index} className="table-row">
                    <div className="rank-col">
                      {index < 3 && <span className="trophy">üèÜ</span>}
                      {index + 1}
                    </div>
                    <div>{score.username}</div>
                    <div>
                      <span className={`score-badge ${
                        index === 0 ? 'gold' : 
                        index === 1 ? 'silver' : 
                        index === 2 ? 'bronze' : 'default'
                      }`}>
                        {score.score}
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ) : (
            <div className="no-scores">No rankings available</div>
          )}
        </div>
      ))}
    </div>
  );
};

export default Leaderboard;
