import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './QuizListTable.css';

function QuizListTable() {
    const [quizzes, setQuizzes] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:8000/api/quizzes/');
                console.log("Fetched quizzes data:", response.data);
                // Handle both array and paginated object response
                if (Array.isArray(response.data)) {
                    setQuizzes(response.data);
                } else if (response.data.results) {
                    setQuizzes(response.data.results);
                } else {
                    setQuizzes([]);
                }
            } catch (error) {
                console.error("Fetch error:", error);
                setError(error);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    if (loading) {
        return <div className="loading">Loading Quizzes...</div>;
    }

    if (error) {
        return <div className="error">Error: {error.message}</div>;
    }

    return (
        <div className="quiz-container">
            <div className="quiz-section">
                <h2>Quiz List</h2>
                <table className="quiz-table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Title</th>
                            <th>Description</th>
                            <th>Created At</th>
                            <th>Questions Count</th>
                        </tr>
                    </thead>
                    <tbody>
                        {quizzes.map((quiz) => (
                            <tr key={quiz.id}>
                                <td>{quiz.id}</td>
                                <td>{quiz.title}</td>
                                <td>{quiz.description}</td>
                                <td>{new Date(quiz.created_At).toLocaleDateString()}</td>
                                <td>{quiz.questions ? quiz.questions.length : 0}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <div className="leaderboard-section">
                <h2>Leaderboard</h2>
                <div className="leaderboard-placeholder">
                    <p>Leaderboard coming soon...</p>
                </div>
            </div>
        </div>
    );
}

export default QuizListTable;
