import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './leaderboard.css';

const Leaderboard = () => {
  const [allScores, setAllScores] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/leaderboard/');
        setAllScores(response.data);
      } catch (err) {
        console.error('Leaderboard error:', err);
        setError('Unable to load leaderboard');
      } finally {
        setLoading(false);
      }
    };

    fetchLeaderboard();
  }, []);

  if (loading) {
    return (
      <div className="d-flex justify-content-center p-4">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger m-3" role="alert">
        Error loading leaderboard: {error}
      </div>
    );
  }

  return (
    <div>
      {Object.entries(allScores).map(([quizName, scores]) => (
        <div key={quizName} className="card mb-4">
          <div className="card-header d-flex justify-content-between align-items-center">
            <h5 className="mb-0">{quizName} - Top users</h5>
            <span className="badge bg-light text-dark">{scores.length} users</span>
          </div>
          {scores.length > 0 ? (
            <div className="card-body p-0">
              <table className="table mb-0">
                <thead>
                  <tr>
                    <th className="rank-column">Rank</th>
                    <th className="player-column">Player</th>
                    <th className="score-column">Score</th>
                  </tr>
                </thead>
                <tbody>
                  {scores.map((score, index) => (
                    <tr key={score.id || index}>
                      <td className="rank-column">{index + 1}</td>
                      <td className="player-column">{score.username}</td>
                      <td className="score-column">
                        <span className="score-badge">{score.score}</span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="card-body text-center text-muted">
              No scores available for {quizName}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default Leaderboard;
