import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './leaderboard.css';

const Leaderboard = ({ quizName }) => {
  const [scores, setScores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const url = quizName 
          ? `http://127.0.0.1:8000/api/leaderboard/${encodeURIComponent(quizName)}/`
          : 'http://127.0.0.1:8000/api/leaderboard/';
        
        const response = await axios.get(url);
        console.log('Leaderboard response:', response.data); // Debug log

        if (quizName && response.data.scores) {
          setScores([{ id: quizName, rank: response.data.scores }]);
        } else if (!quizName && Array.isArray(response.data)) {
          setScores(response.data);
        } else {
          setScores([]);
        }
      } catch (err) {
        console.error('Leaderboard error:', err);
        setError('Unable to load leaderboard');
        setScores([]);
      } finally {
        setLoading(false);
      }
    };

    fetchLeaderboard();
  }, [quizName]);

  if (loading) {
    return (
      <div className="d-flex justify-content-center p-4">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger m-3" role="alert">
        Error loading leaderboard: {error}
      </div>
    );
  }

  return (
    <div className="leaderboard-table">
      {scores.map((score, index) => (
        <div key={score.id} className="leaderboard-row">
          <div className="rank-column">{score.rank}</div>
          <div className="player-column">{score.username}</div>
          <div className="score-column">{score.score}</div>
        </div>
      ))}
    </div>
  );
};

export default Leaderboard;
