import React, { useEffect, useState } from 'react';
import axios from 'axios';


const Leaderboard = ({ quizName }) => {
  const [scores, setScores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const url = quizName 
          ? `http://127.0.0.1:8000/api/leaderboard/${encodeURIComponent(quizName)}/`
          : 'http://127.0.0.1:8000/api/leaderboard/';
        
        const response = await axios.get(url);
        console.log('Leaderboard response:', response.data); // Debug log

        if (quizName && response.data.scores) {
          setScores(response.data.scores);
        } else if (!quizName && Array.isArray(response.data)) {
          setScores(response.data);
        } else {
          setScores([]);
        }
      } catch (err) {
        console.error('Leaderboard error:', err);
        setError('Unable to load leaderboard');
        setScores([]);
      } finally {
        setLoading(false);
      }
    };

    fetchLeaderboard();
  }, [quizName]);

  if (loading) {
    return (
      <div className="d-flex justify-content-center p-4">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger m-3" role="alert">
        Error loading leaderboard: {error}
      </div>
    );
  }

  return (
    <div className="card">
      <div className="card-header bg-primary text-white">
        <h5 className="mb-0">{quizName ? `${quizName} - Top users` : 'Top users'} <span className="float-end">{scores.length} user{scores.length !== 1 ? 's' : ''}</span></h5>
      </div>
      <div className="card-body">
        {scores.length > 0 ? (
          <div className="table-responsive">
            <table className="table table-hover mb-0">
              <thead className="table-light">
                <tr>
                  <th className="text-center" style={{width: "10%"}}>Rank</th>
                  <th style={{width: "50%"}}>Player</th>
                  <th className="text-end" style={{width: "40%"}}>Score</th>
                </tr>
              </thead>
              <tbody>
                {scores.map((score, index) => (
                  <tr key={index}>
                    <td className="text-center">
                      {score.rank}
                      {score.rank <= 3 && <span className="ms-1">👑</span>}
                    </td>
                    <td>
                      <span className="fw-semibold">{score.username}</span>
                    </td>
                    <td className="text-end">
                      <span className={`badge ${score.rank <= 3 ? 'bg-warning text-dark' : 'bg-success'}`}>
                        {score.score}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <div className="p-4 text-center text-muted">
            No scores available for {quizName}
          </div>
        )}
      </div>
    </div>
  );
};

export default Leaderboard;
