import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './leaderboard.css';

const Leaderboard = () => {
  const [allScores, setAllScores] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/leaderboard/');
        setAllScores(response.data);
      } catch (err) {
        console.error('Leaderboard error:', err);
        setError('Unable to load leaderboard');
      } finally {
        setLoading(false);
      }
    };

    fetchLeaderboard();
  }, []);

  if (loading) {
    return (
      <div className="d-flex justify-content-center p-4">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger m-3" role="alert">
        Error loading leaderboard: {error}
      </div>
    );
  }

  return (
    <div>
      {Object.entries(allScores).map(([quizName, scores]) => (
        <div key={quizName} className="leaderboard-container">
          <div className="leaderboard-header">
            <span>{quizName} - Top users</span>
            <span className="user-count">{scores.length} users</span>
          </div>
          <div className="leaderboard-content">
            <div className="leaderboard-table-header">
              <div className="rank-col">Rank</div>
              <div className="player-col">Player</div>
              <div className="score-col">Score</div>
            </div>
            {scores.map((score, index) => (
              <div key={index} className="leaderboard-row">
                <div className="rank-col">{index + 1}</div>
                <div className="player-col">{score.username}</div>
                <div className="score-col">
                  <span className="score-circle">{score.score}</span>
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Leaderboard;
