import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './QuizListTable.css';

function QuizListTable() {
  const [quizzes, setQuizzes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/quizzes/');
        console.log("Fetched quizzes data:", response.data);
        // Handle both array and paginated object response
        if (Array.isArray(response.data)) {
          setQuizzes(response.data);
        } else if (response.data.results) {
          setQuizzes(response.data.results);
        } else {
          setQuizzes([]);
        }
      } catch (error) {
        console.error("Fetch error:", error);
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleStartQuiz = (quizId) => {
    window.location.href = `/quiz?quiz_id=${quizId}`;
  };

  const handleViewDetails = (quiz) => {
    // You can implement a modal or navigation to detail page
    console.log('View details for quiz:', quiz);
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center m-5">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger m-3" role="alert">
        Error loading quizzes: {error.message}
      </div>
    );
  }

  return (
    <div className="quiz-list-box container-fluid py-4">
      <div className="row justify-content-center">
        <div className="col-12 col-xl-10">
          <div className="card shadow">
            <div className="card-header bg-primary text-white d-flex justify-content-between align-items-center">
              <h5 className="card-title mb-0">Available Quizzes</h5>
              <span className="badge bg-light text-primary">{quizzes.length} Quizzes</span>
            </div>
            <div className="card-body">
              <div className="quiz-list-content">
                <div className="quiz-row header">
                  <div className="quiz-title">Quiz Name</div>
                  <div className="quiz-questions">Questions</div>
                  <div className="quiz-action">Action</div>
                </div>
                {quizzes.map((quiz) => (
                  <div key={quiz.id} className="quiz-row">
                    <div className="quiz-title">{quiz.title}</div>
                    <div className="quiz-questions">
                      <span className="question-badge">{quiz.questions_count || 0}</span>
                    </div>
                    <div className="quiz-action">
                      <a href={`/quiz?quiz_id=${quiz.id}`} className="btn btn-primary btn-sm">
                        Start Quiz
                      </a>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default QuizListTable;
