import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Leaderboard = () => {
  const [scores, setScores] = useState([]);

  useEffect(() => {
    // Replace 'sample_quiz' with the actual quiz name you want to fetch
    axios.get('http://localhost:8000/api/leaderboard/sample_quiz/')
      .then(res => {
        setScores(res.data);
      })
      .catch(err => {
        console.error(err);
      });
  }, []);

  return (
    <div>
      <h2>Leaderboard</h2>
      <ul>
        {scores.map((score, index) => (
          <li key={index}>{score.username}: {score.points}</li>
        ))}
      </ul>
    </div>
  );
};

export default Leaderboard;
