import React, { useEffect, useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const Leaderboard = ({ quizId }) => {
  const [scores, setScores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        let url = 'http://127.0.0.1:8000/api/leaderboard/';
        if (quizId) {
          url += `${quizId}/`;
        }
        const response = await axios.get(url);
        if (response.data) {
          const sortedScores = response.data.sort((a, b) => b.points - a.points);
          setScores(sortedScores);
        } else {
          setScores([]);
        }
      } catch (err) {
        console.error('Leaderboard fetch error:', err);
        setError(err.response?.data?.message || 'Failed to load leaderboard');
      } finally {
        setLoading(false);
      }
    };

    fetchLeaderboard();
  }, [quizId]);

  if (loading) {
    return (
      <div className="d-flex justify-content-center p-4">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger m-3" role="alert">
        Error loading leaderboard: {error}
      </div>
    );
  }

  return (
    <div className="card shadow-sm">
      <div className="card-header bg-primary text-white">
        <h5 className="card-title mb-0">Top Players</h5>
      </div>
      <div className="card-body p-0">
        <div className="table-responsive">
          <table className="table table-hover mb-0">
            <thead className="table-light">
              <tr>
                <th className="text-center" style={{width: "10%"}}>#</th>
                <th style={{width: "50%"}}>Player</th>
                <th className="text-end" style={{width: "40%"}}>Score</th>
              </tr>
            </thead>
            <tbody>
              {scores.map((score, index) => (
                <tr key={index}>
                  <td className="text-center">
                    {index + 1}
                  </td>
                  <td>
                    <span className="fw-semibold">{score.username}</span>
                  </td>
                  <td className="text-end">
                    <span className="badge bg-success">{score.points}</span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Leaderboard;
