import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './leaderboard.css';

const Leaderboard = () => {
  const [allScores, setAllScores] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/leaderboard/');
        setAllScores(response.data);
      } catch (err) {
        console.error('Error:', err);
      }
      setLoading(false);
    };

    fetchLeaderboard();
  }, []);

  if (loading) return <div>Loading...</div>;

  return (
    <div>
      {Object.entries(allScores).map(([quizName, scores]) => (
        <div key={quizName} className="leaderboard-box">
          <div className="leaderboard-header">
            <span>{quizName} - Top users</span>
            <span className="user-count">{scores.length} users</span>
          </div>
          {scores.length > 0 ? (
            <div className="leaderboard-content">
              <div className="leaderboard-row header">
                <div className="rank">Rank</div>
                <div className="player">Player</div>
                <div className="score">Score</div>
              </div>
              {scores.map((score, index) => (
                <div key={index} className="leaderboard-row">
                  <div className="rank">{index + 1}</div>
                  <div className="player">{score.username}</div>
                  <div className="score">
                    <span className="score-pill">{score.score}</span>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="no-rankings">No rankings available</div>
          )}
        </div>
      ))}
    </div>
  );
};

export default Leaderboard;
