import React, { useEffect, useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const Leaderboard = ({ quizId = 1 }) => {
  const [scores, setScores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        // Added trailing slash and debug logging
        const url = `http://127.0.0.1:8000/api/quiz/${quizId}/leaderboard/`;
        console.log('Attempting to fetch from:', url);
        
        const response = await axios.get(url);
        console.log('Response received:', response.data);
        
        if (response.data && Array.isArray(response.data)) {
          // Changed to use points instead of score to match the display
          const sortedScores = response.data.sort((a, b) => b.points - a.points);
          setScores(sortedScores);
        } else {
          console.log('No valid data in response:', response.data);
          setScores([]);
        }
      } catch (err) {
        console.error('Detailed error:', {
          message: err.message,
          status: err.response?.status,
          data: err.response?.data
        });
        setError(`Failed to load leaderboard: ${err.message}`);
        setScores([]);
      } finally {
        setLoading(false);
      }
    };

    fetchLeaderboard();
  }, [quizId]);

  if (loading) {
    return (
      <div className="d-flex justify-content-center p-4">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger m-3" role="alert">
        Error loading leaderboard: {error}
      </div>
    );
  }

  return (
    <div className="card shadow-sm">
      <div className="card-header bg-primary text-white">
        <h5 className="card-title mb-0">Top Players</h5>
      </div>
      <div className="card-body p-0">
        {scores.length > 0 ? (
          <div className="table-responsive">
            <table className="table table-hover mb-0">
              <thead className="table-light">
                <tr>
                  <th className="text-center" style={{width: "10%"}}>#</th>
                  <th style={{width: "50%"}}>Player</th>
                  <th className="text-end" style={{width: "40%"}}>Score</th>
                </tr>
              </thead>
              <tbody>
                {scores.map((score, index) => (
                  <tr key={index}>
                    <td className="text-center">
                      {index + 1}
                    </td>
                    <td>
                      <span className="fw-semibold">{score.username}</span>
                    </td>
                    <td className="text-end">
                      <span className="badge bg-success">{score.points}</span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <div className="p-4 text-center text-muted">
            No scores available yet
          </div>
        )}
      </div>
    </div>
  );
};

export default Leaderboard;
